use Peru\Reniec\Dni;

require 'vendor/autoload.php';

$dni = '00000004';

$cs = new Dni();
$person = $cs- get($dni);
if ($person === false) {
    echo $cs- getError();
    exit();
}

echo json_encode(get_object_vars($person));

